pragma solidity ^0.4.23;

import "./ownership/Ownable.sol";
import "./Lottery.sol"; 

/**
 * @title LotteryFactory
 * @dev This is the lottery creator
 */
contract LotteryFactory is Ownable { 
    event LotteryDeployed(address deployedLottery);
    event Owner(address owner);
    event LotteryOwner(address lotteryOwner);
    address public deployedLottery;

    
    //
    /** 
    * @notice (1586381 gas) We launch this to start creating the chain of all contratacts,
    * gets called when a new lottery is created from Lottery.sol
    * @param _duration duration of the lottery
    * @param _lotteryValue value of the lottery
    * @param _reference address of the contract who will create the new one
    *  for first instance, is done manually with the an address(0)
    */
    function createNewLottery(uint256 _duration, uint256 _lotteryValue, address _reference) public returns (Lottery) {
        emit Owner(owner);
        emit LotteryOwner(msg.sender);
        Lottery l = new Lottery(_duration, _lotteryValue, this, _reference);
        l.transferOwnership(msg.sender);
        deployedLottery = address(l);
        emit LotteryDeployed(deployedLottery);
        return l;
    }
}

